$files = scandir('path/to/your/files/');\n$processes = [];\nforeach ($files as $file) {\n    $pid = pcntl_fork();\n    if ($pid == -1) {\n        die('Fork failed');\n    } elseif ($pid) {\n        // Parent process\n        $processes[] = $pid;\n    } else {\n        // Child process\n        processFile('path/to/your/files/' . $file);\n        exit(0);\n    }\n}\n// Wait for child processes to finish\nforeach ($processes as $pid) {\n    pcntl_waitpid($pid, $status);\n}