function tokenize($input) {\n    $tokens = array();\n    $state = 'start';\n    $currentToken = '';\n\n    for ($i = 0; $i < strlen($input); $i++) {\n        $char = $input[$i];\n\n        switch ($state) {\n            case 'start':\n                if (ctype_digit($char)) {\n                    $state = 'number';\n                    $currentToken = $char;\n                } elseif (in_array($char, array('+', '-', '*', '/'))) {\n                    $tokens[] = $char;\n                } else {\n                    // handle whitespace or other characters\n                }\n                break;\n            case 'number':\n                if (ctype_digit($char)) {\n                    $currentToken .= $char;\n                } else {\n                    $tokens[] = $currentToken;\n                    $state = 'start';\n                    $currentToken = '';\n                    $i--; // re-examine the current character\n                }\n                break;\n        }\n    }\n\n    if ($state == 'number') {\n        $tokens[] = $currentToken;\n    }\n\n    return $tokens;\n}\n\n$input = "2 + 3 * 4";\n$tokens = tokenize($input);\n\nprint_r($tokens);