// Ensure $txt_body and $html_body are UTF-8 encoded\n$txt_body = utf8_encode(file_get_contents(...));\n$html_body = utf8_encode(file_get_contents(...));\n\n// Replace IPN info in $txt_body and $html_body\n// ...\n\n// Set headers with UTF-8 encoding\n$header = "From: \n";\n$header .= "Reply-To: \n";\n$header .= "Content-Type: multipart/alternative; boundary=$alt_boundary; charset=UTF-8\n";\n$header .= "Mime-Version: 1.0\n";\n$header .= "X-Mailer: PHP/".phpversion()."\n";\n\n// Set body parts with UTF-8 encoding\n$body .= "\n".wordwrap($txt_body, 70);\n\n$body .= "\n\n--$alt_boundary\n";\n$body .= "Content-Type: multipart/mixed; boundary=$mixed_boundary\n";\n\n$body .= "\n\n\n--$mixed_boundary\n";\n$body .= "Content-Type: text/html; charset=UTF-8\n";\n$body .= "Content-Transfer-Encoding: 8bit\n"; // Use 8bit instead of 7bit for UTF-8\n\n$body .= "\n".wordwrap($html_body, 70);\n\n// ...