// Model\nclass UserModel {\n    public function getUsers() {\n        $results = array();\n        $database_result_resource = // fetch data from database\n        while ($row = some_fetching_function($database_result_resource)) {\n            $results[] = $row;\n        }\n        return $results;\n    }\n}\n\n// Controller\nclass UserController {\n    public function index() {\n        $userModel = new UserModel();\n        $users = $userModel->getUsers();\n        $this->view->render('users', array('users' => $users));\n    }\n}\n\n// View\n<?php foreach ($users as $user): ?>\n    <tr>\n        <td><?= $user['Username'] ?></td>\n        ...etc\n    </tr>\n<?php endforeach; ?>