// Assuming you're using PDO and Redis for caching\n$pdo = new PDO('mysql:host=localhost;dbname=mydb', 'username', 'password');\n$redis = new Redis();\n$redis->connect('localhost', 6379);\n\n// Function to retrieve configuration value from database with caching\nfunction getConfigValue($name) {\n    $cacheKey = 'config_'.$name;\n    if ($redis->exists($cacheKey)) {\n        return $redis->get($cacheKey);\n    }\n    $stmt = $pdo->prepare('SELECT value FROM config WHERE name = :name');\n    $stmt->execute(['name' => $name]);\n    $value = $stmt->fetchColumn();\n    $redis->set($cacheKey, $value);\n    return $value;\n}\n\n// Access configuration value\necho getConfigValue('my_var');