function combine($arrays, $callback) {\n    if (empty($arrays)) {\n        return;\n    }\n    $current = array_shift($arrays);\n    foreach ($current as $value) {\n        if (empty($arrays)) {\n            $callback($value);\n        } else {\n            combine($arrays, function ($combination) use ($value, $callback) {\n                $callback(array_merge([$value], $combination));\n            });\n        }\n    }\n}\n\n$as = [$optionA1, $optionA2];\n$bs = [$optionB1, $optionB2];\n$cs = [$optionC1, $optionC2];\n\ncombine([$as, $bs, $cs], function ($combination) {\n    list($a, $b, $c) = $combination;\n    $result = $this->method($a, $b, $c);\n    if ($result) {\n        // ...\n    }\n});