$trie = new Trie();\n// populate the Trie with words\n\n$board = [\n    ['A', 'B', 'C'],\n    ['D', 'E', 'F'],\n    ['G', 'H', 'I']\n];\n\n$words = [];\nfor ($i = 0; $i < count($board); $i++) {\n    for ($j = 0; $j < count($board[$i]); $j++) {\n        $word = '';\n        for ($k = 0; $k < 8; $k++) { // 8 possible directions\n            $word .= $board[$i][$j];\n            if ($trie->search($word)) {\n                $words[] = $word;\n            }\n            // move to next character in the direction\n            $i += $k % 3;\n            $j += floor($k / 3);\n            if ($i < 0 || $i >= count($board) || $j < 0 || $j >= count($board[$i])) {\n                break;\n            }\n        }\n    }\n}