require_once 'HTMLPurifier.auto.php';\n\n$userInput = '*strong*\n_em_\n[blockquote]\nhttp://www.link.com';\n$allowedTags = array('strong', 'em', 'blockquote', 'a');\n$allowedAttributes = array('href');\n\n$config = HTMLPurifier_Config::createDefault();\n$config->set('HTML.AllowedElements', implode(',', $allowedTags));\n$config->set('HTML.AllowedAttributes', implode(',', $allowedAttributes));\n\n$purifier = new HTMLPurifier($config);\n$purifiedHtml = $purifier->purify($userInput);\n\necho $purifiedHtml;\n// Output: <strong>strong</strong><br /><em>em</em><br /><blockquote>http://www.link.com</blockquote>