private function generateTag($tag_id) {\n    $tag = $this->tags[$tag_id];\n\n    // Generate the HTML output for the current tag\n    $output = '<' . $tag['tag'];\n\n    // Add attributes\n    if (isset($tag['attrs'])) {\n        foreach ($tag['attrs'] as $key => $value) {\n            $output .= ' ' . $key . '="' . htmlspecialchars($value) . '"';\n        }\n    }\n\n    // Close the opening tag\n    $output .= '>';\n\n    // Add child tags\n    if (isset($tag['children'])) {\n        foreach ($tag['children'] as $child_id) {\n            $output .= $this->generateTag($child_id);\n        }\n    }\n\n    // Close the current tag\n    $output .= '</' . $tag['tag'] . '>';\n\n    return $output;\n}