function is_spam($post) {\n    $score = 0;\n    if (!validate_captcha($post['captcha_response'])) {\n        $score += 2;\n    }\n    if (is_rate_limited($post['ip_address'])) {\n        $score += 1;\n    }\n    if (is_spam_content($post['content'])) {\n        $score += 3;\n    }\n    if (is_spam_ip($post['ip_address'])) {\n        $score += 2;\n    }\n    if (is_suspicious_user_agent($post['user_agent'])) {\n        $score += 1;\n    }\n    return $score >= 5; // adjust this threshold value\n}