// Assuming you have a PDO object named $pdo\n\n// Prepare the SELECT query with a parameter\n$stmt = $pdo->prepare('SELECT id FROM table_name WHERE name = :name');\n\n// Bind the parameter to the query\n$stmt->bindParam(':name', $name);\n\n// Set the value of the parameter\n$name = 'John Doe';\n\n// Execute the query\n$stmt->execute();\n\n// Fetch the result\n$result = $stmt->fetch();\n\n// Check if the query was successful\nif ($result !== false) {\n    $id = $result['id'];\n    // Use the $id for the INSERT query\n    // ...\n} else {\n    // Handle the error\n    // ...\n}