// Database/UserDatabase.php\n<?php\nuse PDO;\n\nclass UserDatabase\n{\n    private $pdo;\n    private $queries;\n\n    public function __construct(PDO $pdo)\n    {\n        $this->pdo = $pdo;\n        $this->queries = include 'sql/queries.php';\n    }\n\n    public function getUser($id)\n    {\n        $stmt = $this->pdo->prepare($this->queries['getUser']);\n        $stmt->bindParam(':id', $id);\n        $stmt->execute();\n        return $stmt->fetch();\n    }\n}