function deleteOldFiles($dir, $days) {\n  $mydir = opendir($dir);\n  while(($file = readdir($mydir)) !== false) {\n    $filePath = realpath($dir . DIRECTORY_SEPARATOR . $file);\n    if($file != "." && $file != ".." && (filemtime($filePath) <= time() - ($days * 86400))) {\n      //unlink($filePath) or DIE("Failed to delete $dir$file<br />");\n      echo filemtime($filePath);\n    }\n  }\n  closedir($mydir);\n}