function deleteOldFiles($dir, $days) {\n  $mydir = opendir($dir);\n  while(($file = readdir($mydir)) !== false) {\n    if($file != "." && $file != ".." && (filemtime($dir . DIRECTORY_SEPARATOR . $file) <= time() - ($days * 86400))) {\n      //unlink($dir . DIRECTORY_SEPARATOR . $file) or DIE("Failed to delete $dir$file<br />");\n      echo filemtime($dir . DIRECTORY_SEPARATOR . $file);\n    }\n  }\n  closedir($mydir);\n}