function timeAgo($timestamp) {\n  $time = time() - $timestamp;\n  $units = array(\n    31536000 => 'year',\n    2592000 => 'month',\n    604800 => 'week',\n    86400 => 'day',\n    3600 => 'hour',\n    60 => 'minute',\n    1 => 'second'\n  );\n\n  foreach ($units as $unit => $text) {\n    if ($time < $unit) continue;\n    $numberOfUnits = floor($time / $unit);\n    return $numberOfUnits . ' ' . $text . ($numberOfUnits > 1 ? 's' : '') . ' ago';\n  }\n}\n\n// Usage\n$timestamp = strtotime("2022-01-01 12:00:00"); // replace with your timestamp\necho timeAgo($timestamp);