// Set cookie\nfunction set_cookie($name, $value) {\n    setcookie($name, $value, time() + (86400 * 30), "/"); // 86400 = 1 day\n}\n\n// Ajax handler\nif (isset($_POST['action']) && $_POST['action'] == 'set_data') {\n    // Collect data...\n    $data = array('some' => 'data');\n    \n    // Set cookie\n    set_cookie('my_cookie', 'some_value');\n    \n    // Return data\n    echo json_encode($data);\n}