<?php\nheader('Content-Type: text/plain');\n\n// Connect to database using mysqli\n$mysqli = new mysqli('localhost', 'root', 'xxxx');\n\nif ($mysqli->connect_error) {\n    wText('Server connection failed: ' . $mysqli->connect_error);\n    exit;\n}\n\n// Create database\n$sql = 'CREATE DATABASE tf2faq';\nif ($mysqli->query($sql)) {\n    wText('Database tf2faq created');\n} else {\n    wText('Database tf2faq create failed: ' . $mysqli->error);\n}\n\n// Create table\n$sql = 'CREATE TABLE `tf2faq`.`loadoutitems` (\n  `item` TEXT NULL,\n  `class` TEXT NULL,\n  `type` TEXT NULL,\n  `replaces` TEXT NULL,\n  `itempos` TEXT NULL,\n  `modpos` TEXT NULL,\n  `modneg` TEXT NULL,\n  `imgurl` TEXT NULL,\n  `notes` TEXT NULL\n) ENGINE = MyISAM';\nif ($mysqli->query($sql)) {\n    wText('Table loadoutitems created');\n} else {\n    wText('Table loadoutitems create failed: ' . $mysqli->error);\n}\n\n// Load data from file\n$sql = "LOAD DATA LOCAL INFILE 'loadoutitmsfx.csv' INTO TABLE `tf2faq`.`loadoutitems`";\nif ($mysqli->query($sql)) {\n    wText('Data Load Passed');\n} else {\n    wText('Data Load Failed: ' . $mysqli->error);\n}\n\nfunction wText($txt) {\n    echo $txt . "\n";\n}\n?>