$boundary = md5(time());\n$mime_boundary = "--==Multipart_Boundary_".$boundary;\n\n$headers = array(\n    "MIME-Version: 1.0",\n    "Content-Type: multipart/mixed; boundary=\"$mime_boundary\""\n);\n\n$message = "This is a multi-part message in MIME format.\n\n";\n$message .= "$mime_boundary\n";\n$message .= "Content-Type: text/plain; charset=\"iso-8859-1\"\n";\n$message .= "Content-Transfer-Encoding: 7bit\n\n";\n$message .= "my message text\n\n";\n\n$message .= "$mime_boundary\n";\n$message .= "Content-Type: image/gif; name=\"image1.gif\"\n";\n$message .= "Content-Disposition: attachment; filename=\"image1.gif\"\n";\n$message .= "Content-Transfer-Encoding: base64\n\n";\n$message .= base64_encode(file_get_contents("image1.gif"))."\n\n";\n\n$message .= "$mime_boundary\n";\n$message .= "Content-Type: image/gif; name=\"image2.gif\"\n";\n$message .= "Content-Disposition: attachment; filename=\"image2.gif\"\n";\n$message .= "Content-Transfer-Encoding: base64\n\n";\n$message .= base64_encode(file_get_contents("image2.gif"))."\n\n";\n\n$message .= "$mime_boundary--";\n\nmail("recipient@example.com", "Test Email", $message, implode("\r\n", $headers));