function fetch($remoteFile, $localFile) {\n    $fp = fopen($remoteFile, 'rb');\n    if (!$fp) {\n        throw new Exception("Failed to open remote file $remoteFile");\n    }\n    $localFp = fopen($localFile, 'wb');\n    if (!$localFp) {\n        throw new Exception("Failed to open local file $localFile");\n    }\n    stream_copy_to_stream($fp, $localFp);\n    fclose($fp);\n    fclose($localFp);\n}