function browse() {\n    $categories = new categories;\n    $categories_list = $categories->build_list();\n\n    $categories_array = array();\n    foreach($categories_list as $c) {\n        $categories_array[] = array(\n            'id' => $c['id'],\n            'name' => $c['name'],\n            'subcategories' => $c['subcategories']\n        );\n    }\n\n    return $categories_array;\n}