$allowedFunctions = [\n    'myfunc' => function($a, $b, $c) { /* implementation */ },\n    'anotherFunc' => function($a, $b, $c) { /* implementation */ },\n];\n\n$code = fetchCodeFromDatabase(); // e.g., "myfunc(1,2,3); anotherFunc(3,2,1);"\n$parts = explode(';', $code);\nforeach ($parts as $part) {\n    $funcName = trim(substr($part, 0, strpos($part, '(')));\n    if (isset($allowedFunctions[$funcName])) {\n        $args = explode(',', substr($part, strpos($part, '(') + 1, -1));\n        call_user_func_array($allowedFunctions[$funcName], $args);\n    } else {\n        // handle invalid function name\n    }\n}