$min = 1; // minimum value\n$max = 100; // maximum value\n$qty = 10; // number of unique random numbers to generate\n\n$randomNumbers = [];\nfor ($i = 0; $i < $qty; $i++) {\n    $randomNumber = random_int($min, $max);\n    while (in_array($randomNumber, $randomNumbers)) {\n        $randomNumber = random_int($min, $max);\n    }\n    $randomNumbers[] = $randomNumber;\n}\n\nprint_r($randomNumbers);