$consumer = new Zend_OpenId_Consumer();\n$providerUrl = 'https://www.google.com/accounts/o8/id'; // or Yahoo's equivalent\n\n// Redirect the user to the provider's authentication URL\n$redirectUrl = $consumer->getRedirectUrl($providerUrl, 'http://your-site.com/openid/callback');\nheader('Location: ' . $redirectUrl);\nexit;\n\n// Handle the authentication response\n$callbackUrl = 'http://your-site.com/openid/callback';\n$response = $consumer->getResponse($callbackUrl);\nif ($response->isValid()) {\n    $identity = $response->getIdentity();\n    // User is authenticated, use their identity as needed\n}