function list_recursive($dir) {\n    $files = scandir($dir);\n    foreach ($files as $file) {\n        if ($file == '.' || $file == '..') continue;\n        $path = "$dir/$file";\n        echo "$path\n";\n        if (is_dir($path)) {\n            list_recursive($path);\n        }\n    }\n}\n\nfunction list_iterative($from) {\n    $dirs = array($from);\n    while (NULL !== ($dir = array_pop($dirs))) {\n        $files = scandir($dir);\n        foreach ($files as $file) {\n            if ($file == '.' || $file == '..') continue;\n            $path = "$dir/$file";\n            echo "$path\n";\n            if (is_dir($path)) {\n                $dirs[] = $path;\n            }\n        }\n    }\n}\n\nfunction list_glob($dir) {\n    $files = glob("$dir/*");\n    foreach ($files as $file) {\n        echo "$file\n";\n        if (is_dir($file)) {\n            list_glob($file);\n        }\n    }\n}