<?php\n// Connect to MySQL database\n$conn = mysql_connect('your_host', 'your_username', 'your_password');\nmysql_select_db('your_database', $conn);\n\n// Query the database\n$query = "SELECT your_column_name FROM your_table_name";\n$result = mysql_query($query, $conn);\n\n// Store query results in an array\n$data = array();\nwhile ($row = mysql_fetch_assoc($result)) {\n    $data[] = $row['your_column_name'];\n}\n\n// Encode array as JSON\n$json_data = json_encode($data);\n\n// Output JSON data\necho $json_data;\n\n// Close MySQL connection\nmysql_close($conn);\n?>