$usernameQuery = "SELECT username, firstname FROM USERS WHERE username = ?";\n\n$stmt = $con->prepare($usernameQuery);\n$stmt->bind_param("s", $username);\n$stmt->execute();\n$stmt->bind_result($checkUsername, $checkFirstName);\n$stmt->fetch();\n\necho "<form name=\"userForm\">\n<h1>Editing information for: " . htmlspecialchars($username) . "</h1>\n<p>\nFirst name:\n<input type=\"text\" id=\"firstname\" value=\"" . htmlspecialchars($checkFirstName) . "\">\n<br />\n</form>";