class ReportField\n{\n    private $key;\n    private $label;\n    private $accessLevel;\n    private $sqlDescriptor;\n\n    private function __construct($key, $label, $accessLevel, $sqlDescriptor)\n    {\n        $this->key = $key;\n        $this->label = $label;\n        $this->accessLevel = $accessLevel;\n        $this->sqlDescriptor = $sqlDescriptor;\n    }\n\n    public static function FOO()\n    {\n        return new self('foo', 'Foo', 1, 'foo as my_foo');\n    }\n\n    // Add more static methods for other fields\n\n    public static function getByKey($key)\n    {\n        $fields = [\n            'foo' => self::FOO(),\n            // Add more fields to the array\n        ];\n\n        return $fields[$key] ?? null;\n    }\n\n    public function getFieldSql()\n    {\n        return $this->sqlDescriptor;\n    }\n}