function getDirectLink($url) {\n    $ch = curl_init($url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    curl_setopt($ch, CURLOPT_HEADER, false);\n    $response = curl_exec($ch);\n    curl_close($ch);\n\n    $dom = new DOMDocument();\n    @$dom->loadHTML($response);\n    $xpath = new DOMXPath($dom);\n    $link = $xpath->query("//div[@id='download_link']/a")->item(0)->getAttribute('href');\n\n    return $link;\n}\n\n$url = "http://www.mediafire.com/?your_file_id"; // replace with your file id\n$directLink = getDirectLink($url);\necho $directLink;