static public function prepare($var)\n{\n    if (is_null($var)) {\n        return 'NULL';\n    } elseif (is_bool($var)) {\n        return (int)$var;\n    } elseif (is_int($var)) {\n        return $var;\n    } elseif (is_string($var) || is_float($var)) {\n        return "'" . Database::escape($var) . "'";\n    } else {\n        throw new Exception('Unsupported variable type [' . gettype($var) . ']');\n    }\n}