<?php\n$mysqli = new mysqli('your_host', 'your_username', 'your_password', 'your_database');\n\n$current_year = date('Y');\n$current_index = 1;\n\n// Check if the year has changed\n$result = $mysqli->query("SELECT current_year FROM index_control");\nif ($result->num_rows > 0) {\n  $row = $result->fetch_assoc();\n  if ($row['current_year'] != $current_year) {\n    // Update the year and reset the index\n    $mysqli->query("UPDATE index_control SET current_year = $current_year, current_index = $current_index");\n  }\n} else {\n  // Initialize the index_control table\n  $mysqli->query("INSERT INTO index_control (current_year, current_index) VALUES ($current_year, $current_index)");\n}\n\n// Generate the next index\n$next_index = sprintf("%04d%05d", $current_year, $current_index);\n\n// Use the next index in your database operations...