$mysqli = new mysqli("your_host", "your_user", "your_password", "your_database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$query = "SELECT COUNT(DISTINCT CONCAT(IP, userid)) as online_users FROM `bigBrother` WHERE datetime BETWEEN DATE_SUB(NOW(), INTERVAL 3 MINUTE) AND NOW()";\n\n$result = $mysqli->query($query);\n\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        echo "Online users: " . $row["online_users"];\n    }\n} else {\n    echo "0 results";\n}\n\n$mysqli->close();