<?php\n\n// IMAP server settings\n$host = 'your_imap_host';\n$username = 'your_imap_username';\n$password = 'your_imap_password';\n\n// Connect to IMAP server\n$mailbox = imap_open("{{$host}/imap/ssl}INBOX", $username, $password);\n\n// Check if connection was successful\nif ($mailbox) {\n    // Search for the most recent email\n    $search = imap_search($mailbox, 'ALL', '', '', 'REVERSE ON');\n    $msgs = imap_fetch_overview($mailbox, $search[0]);\n\n    // Get the body of the most recent email\n    $body = imap_fetchbody($mailbox, $msgs[0]->msgno, '');\n\n    // Print the body\n    echo $body;\n\n    // Close the IMAP connection\n    imap_close($mailbox);\n} else {\n    echo "Failed to connect to IMAP server";\n}\n\n?>