// Assuming you have a table named 'records' with a column named 'name'\n\n// Get the current record's name and its adjacent records\n$current_name = 'Dunn'; // Replace with the actual current record's name\n\n$query = "SELECT name \n          FROM records \n          WHERE name <= '$current_name' \n          ORDER BY name DESC \n          LIMIT 4";\n\n$prev_records = mysqli_query($conn, $query);\n\n$query = "SELECT name \n          FROM records \n          WHERE name > '$current_name' \n          ORDER BY name ASC \n          LIMIT 3";\n\n$next_records = mysqli_query($conn, $query);\n\n// Print the links\necho '<ul>';\nwhile ($prev_record = mysqli_fetch_assoc($prev_records)) {\n    echo '<li>' . $prev_record['name'] . '</li>';\n}\necho '<li><strong>' . $current_name . '</strong></li>';\nwhile ($next_record = mysqli_fetch_assoc($next_records)) {\n    echo '<li>' . $next_record['name'] . '</li>';\n}\necho '</ul>';