// Use a secure way to download the image\n$opts = array(\n    'http' => array(\n        'method' => "GET",\n        'header' => "User-Agent: My PHP Script\r\n"\n    )\n);\n\n// Create a stream context\n$context = stream_context_create($opts);\n\n// Use the context to download the image\n$imageData = file_get_contents($PIC_URL, false, $context);\n\n// Check if the download was successful\nif ($imageData !== false) {\n    // Save the image\n    file_put_contents($pk, $imageData);\n} else {\n    // Handle the error\n    error_log("Failed to download image from $PIC_URL");\n}