if ($word_of_the_day) {\n    $wotd = $wpdb->get_results("SELECT term, definition FROM glossary WHERE term = :term", array('term' => $word_of_the_day));\n    foreach ($wotd as $term) { }\n} elseif ($_COOKIE['WOTD']) {\n    $word_of_the_day = filter_var($_COOKIE['WOTD'], FILTER_SANITIZE_STRING);\n    $wotd = $wpdb->get_results("SELECT term, definition FROM glossary WHERE term = :term", array('term' => $word_of_the_day));\n    foreach ($wotd as $term) { }\n} else {\n    $wotd = $wpdb->get_results("SELECT term, definition FROM glossary ORDER BY RAND() LIMIT 1");\n    foreach ($wotd as $term) {\n        setcookie("WOTD", $term->term, time()+86400);\n    }\n}