$xml = simplexml_load_file('http://www.wowarmory.com/character-reputation.xml?r=Realm&cn=CharacterName');\n\nforeach ($xml->faction as $faction) {\n    $currentReputation = (int) $faction['reputation'];\n    $maxReputation = 45000;\n    $reputationPercentage = ($currentReputation / $maxReputation) * 100;\n\n    echo "Faction: {$faction['name']}, Current Reputation: $currentReputation, Reputation Percentage: $reputationPercentage%\n";\n}