function getComposedTerms($sentence, $limit = 3) {\n    $words = explode(' ', $sentence);\n    $terms = [];\n\n    for ($i = 0; $i < count($words); $i++) {\n        for ($j = 1; $j <= $limit; $j++) {\n            if ($i + $j <= count($words)) {\n                $term = implode(' ', array_slice($words, $i, $j));\n                $terms[] = $term;\n            }\n        }\n    }\n\n    return $terms;\n}\n\n// Example usage:\n$sentence = "John Doe moved to New York last year.";\n$terms = getComposedTerms($sentence, 3);\nprint_r($terms);