function getaim($screenname) {\n    $ch     = curl_init();\n    $url    = "http://big.oscar.aol.com/$screenname?on_url=true&off_url=false";\n    curl_setopt($ch, CURLOPT_URL,$url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER,1);\n    curl_setopt($ch, CURLOPT_HEADER, 1);\n    curl_setopt($ch, CURLOPT_NOBODY, 1); // use HEAD method\n    curl_setopt($ch, CURLOPT_TIMEOUT, 2); // timeout after 2 seconds\n    $result = curl_exec($ch);\n    curl_close($ch);\n\n    if(eregi("true",$result)) {\n        return true;\n    } else {\n        return false;\n    }\n}