// Check if the request is a multipart/form-data request\nif ($_SERVER['CONTENT_TYPE'] === 'multipart/form-data') {\n    // Get the boundary from the Content-Type header\n    $boundary = substr($_SERVER['CONTENT_TYPE'], strpos($_SERVER['CONTENT_TYPE'], 'boundary=') + 9);\n\n    // Read the raw request data from the php://input stream\n    $rawData = file_get_contents('php://input');\n\n    // Split the raw data into parts using the boundary\n    $parts = explode("--$boundary", $rawData);\n\n    // Process each part\n    foreach ($parts as $part) {\n        // Check if the part is a file\n        if (strpos($part, 'Content-Type: application/octet-stream') !== false) {\n            // Get the file contents\n            $fileContents = substr($part, strpos($part, "\r\n\r\n") + 4);\n\n            // Do something with the file contents\n            echo "Received file contents: $fileContents\n";\n        } elseif (strpos($part, 'Content-Type: text/xml') !== false) {\n            // Get the XML data\n            $xmlData = substr($part, strpos($part, "\r\n\r\n") + 4);\n\n            // Do something with the XML data\n            echo "Received XML data: $xmlData\n";\n        }\n    }\n}