class Database {\n    private $logger;\n\n    public function __construct(LoggerInterface $logger) {\n        $this->logger = $logger;\n    }\n\n    public function doInsert($insert) {\n        // implementation\n        $this->logger->logDatabaseQuery($insert);\n    }\n\n    public function runQuery($query) {\n        // implementation\n        $this->logger->logDatabaseQuery($query);\n    }\n}