use Doctrine\ORM\Tools\Setup;\nuse Doctrine\ORM\EntityManager;\n\nrequire_once "vendor/autoload.php";\n\n// Create a simple "default" Doctrine ORM configuration for Annotations\n$config = Setup::createAnnotationMetadataConfiguration(array(__DIR__."/entities"), true);\n\n// Create an EntityManager\n$entityManager = EntityManager::create(array('driver' => 'pdo_sqlite', 'path' => __DIR__.'/example.db'), $config);\n\n// Define an entity\n/**\n * @Entity\n * @Table(name="users")\n */\nclass User {\n    /**\n     * @Id @GeneratedValue\n     * @Column(type="integer")\n     */\n    private $id;\n\n    /**\n     * @Column(type="string")\n     */\n    private $name;\n\n    // Getters and setters\n}\n\n// Use the EntityManager to interact with the User entity\n$user = new User();\n$user->setName('John Doe');\n$entityManager->persist($user);\n$entityManager->flush();