// UserRepository.php\n\npublic function getUserBasicInfo($id)\n{\n    $query = $this->em->createQuery('SELECT u.id, u.name FROM User u WHERE u.id = :id');\n    $query->setParameter('id', $id);\n    return $query->getOneOrNullResult();\n}\n\npublic function getUserDetailedInfo($id)\n{\n    $query = $this->em->createQuery('SELECT u.id, u.name, u.email, u.address FROM User u WHERE u.id = :id');\n    $query->setParameter('id', $id);\n    return $query->getOneOrNullResult();\n}