// Controller\npublic function viewAction()\n{\n    $isMobile = $this->_getParam('mobile', false);\n    $entry = $this->_getModel()->getEntry();\n    $comments = $this->_getModel()->getComments();\n\n    if ($isMobile) {\n        // Render mobile view\n        $this->view->mobile = true;\n        $this->view->entry = $entry;\n        $this->view->comments = $comments;\n        $this->render('mobile-view');\n    } else {\n        // Render regular view\n        $this->view->entry = $entry;\n        $this->view->comments = $comments;\n        $this->render('regular-view');\n    }\n}\n\n// In your routing configuration\nroutes.view.route = "/blog/:id/:mobile"\nroutes.view.defaults.controller = "blog"\nroutes.view.defaults.action = "view"\nroutes.view.defaults.mobile = false