// Add custom fields to the comment form\nfunction add_custom_fields_to_comment_form() {\n    echo '<p><label for="friend_name">Friend\'s name:</label> <input type="text" id="friend_name" name="friend_name" /></p>';\n    echo '<p><label for="friend_email">Friend\'s email:</label> <input type="email" id="friend_email" name="friend_email" /></p>';\n}\nadd_action('comment_form_after_fields', 'add_custom_fields_to_comment_form');\n\n// Hook into the comment submission process\nfunction send_comment_to_friend($comment_id) {\n    $comment = get_comment($comment_id);\n    $friend_name = $_POST['friend_name'];\n    $friend_email = $_POST['friend_email'];\n    $message = $comment->comment_content;\n\n    // Sanitize and validate the friend's email address\n    $friend_email = sanitize_email($friend_email);\n    if (!is_email($friend_email)) {\n        return;\n    }\n\n    // Send the email\n    $subject = 'Your friend mentioned you';\n    $body = "Hey $friend_name,\n\n" . $message;\n    wp_mail($friend_email, $subject, $body);\n}\nadd_action('comment_post', 'send_comment_to_friend');