class Example {\n    private $name;\n\n    public function __get($property) {\n        if ($property === 'name') {\n            return $this->name;\n        }\n        // handle other properties or throw an exception\n    }\n\n    public function __set($property, $value) {\n        if ($property === 'name') {\n            $this->name = $value;\n        }\n        // handle other properties or throw an exception\n    }\n}