$fontFile = 'path/to/font.ttf'; // ~200KB font file\n$fontSize = 24;\n\n$image = imagecreatetruecolor(200, 50);\nimagecolorallocate($image, 255, 255, 255); // white background\n\n$font = imageloadfont($fontFile);\nimagestring($image, $font, 10, 10, 'Your CAPTCHA text', imagecolorallocate($image, 0, 0, 0)); // black text\n\nheader('Content-Type: image/png');\nimagepng($image);\nimagedestroy($image);