if (isset($_COOKIE["selectedStyle"])) {\n    $style = $_COOKIE["selectedStyle"];\n} else {\n    $style = "spring";\n}\n\nif (isset($_COOKIE["selectedFontSize"])) {\n    $fontSize = $_COOKIE["selectedFontSize"];\n} else {\n    $fontSize = "standard";\n}\n\nif (isset($_POST["changeStyle"])) {\n    $style = $_POST["changeStyle"];\n}\n\nif (isset($_POST["changeFontSize"])) {\n    $fontSize = $_POST["changeFontSize"];\n}\n\nsetcookie("selectedStyle", $style, time() + 86400);\nsetcookie("selectedFontSize", $fontSize, time() + 86400);\n\n// Load the selected stylesheets\necho "<link rel='stylesheet' type='text/css' href='" . $style . ".css'>";\necho "<link rel='stylesheet' type='text/css' href='" . $fontSize . ".css'>";