$query = "SELECT a, b, c, d, e FROM table";\n$stmt = $this->conn->prepare($query);\n$stmt->execute();\n\n// Get the number of columns\n$columns = $stmt->field_count;\n\n// Initialize an empty array to store the results\n$results = [];\n\n// Loop through the columns and bind the results\nfor ($i = 0; $i < $columns; $i++) {\n    $stmt->bind_result($results[$i]);\n}\n\n// Fetch the results\nwhile ($stmt->fetch()) {\n    // Do something with the results\n    print_r($results);\n}\n\n$stmt->close();