$allowedExtensions = array('gif', 'jpg', 'png', 'pdf', 'doc', 'wmv');\n$allowedMimeTypes = array('image/gif', 'image/jpeg', 'image/png', 'application/pdf', 'application/msword', 'video/x-ms-wmv');\n\n$file = $_FILES['file'];\n$extension = strtolower(pathinfo($file['name'], PATHINFO_EXTENSION));\n$mimeType = finfo_file(finfo_open(FILEINFO_MIME_TYPE), $file['tmp_name']);\n\nif (!in_array($extension, $allowedExtensions) || !in_array($mimeType, $allowedMimeTypes)) {\n    // File type or MIME type not allowed\n    exit('Invalid file type');\n}