// Assuming you have a 'blogs' table with columns 'id', 'user_id', 'created_at'\n// and a 'users' table with columns 'id', 'joined_at'\n\n// Get the user's join date\n$user_id = $_GET['user_id']; // or however you get the user ID\n$user_join_date = mysql_fetch_assoc(mysql_query("SELECT joined_at FROM users WHERE id = '$user_id'"));\n$user_join_date = $user_join_date['joined_at'];\n\n// Get the current date\n$current_date = date('Y-m-d');\n\n// Get the list of months\n$months = mysql_query("\n    SELECT DISTINCT YEAR(created_at) AS year, MONTH(created_at) AS month\n    FROM blogs\n    WHERE user_id = '$user_id'\n    AND created_at >= '$user_join_date'\n    AND created_at <= '$current_date'\n    ORDER BY year DESC, month DESC\n");\n\n// Print the list of months\nwhile ($month = mysql_fetch_assoc($months)) {\n    $month_name = date('F', mktime(0, 0, 0, $month['month'], 1, $month['year']));\n    echo "<a href='#'>$month_name $month[year]</a><br>";\n}